function kruskal(edges, vertices) {
  const mst = [];
  edges.sort((a, b) => a.weight - b.weight);
  const parent = {};

  // Initialize Union-Find
  vertices.forEach(v => parent[v] = v);

  function find(v) {
    if (parent[v] !== v) parent[v] = find(parent[v]);
    return parent[v];
  }

  for (let edge of edges) {
    const root1 = find(edge.src);
    const root2 = find(edge.dest);
    if (root1 !== root2) {
      mst.push(edge);
      parent[root1] = root2;
    }
  }
  return mst;
}

