      const parentIndex = Math.floor((index - 1) / 2);
      if (arr[parentIndex] <= arr[index]) break;
      [arr[parentIndex], arr[index]] = [arr[index], arr[parentIndex]];
      index = parentIndex;
    }
  }

  function heapifyDown(arr, index) {
    const length = arr.length;
    while (true) {
      let smallest = index;
      const left = 2 * index + 1;
      const right = 2 * index + 2;

      if (left < length && arr[left] < arr[smallest]) smallest = left;
      if (right < length && arr[right] < arr[smallest]) smallest = right;
      if (smallest === index) break;

      [arr[index], arr[smallest]] = [arr[smallest], arr[index]];
      index = smallest;
